package Lec1;

public class Arithmetic_Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 9;
		int b = 7;
		int c = a + b;
		System.out.println(c);
		c = a - b;
		System.out.println(c);
		int d = c + a;
		System.out.println(d);
		int x = 9;
		int y = 11;
		int z = x * y;
		System.out.println(z);

		int p = 89;
		int q = 7;
		int m = p / q;
		System.out.println(m);
		
		int k = 76;
		int l=8;
		int r = k%l;
		System.out.println(r);

	}

}
