package Lec63;

public class Right_Shift_Left_Shift {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 56;
		System.out.println(x >> 3);// right shift
		int y = -5;
		System.out.println(y >> 1);// right shift
		System.out.println(x << 2);// left shift
		System.out.println(y << 2);// left shift

	}

}
